import react, { useEffect, useState } from "react";

function Form() {
  // Usestate
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const[text, setText]=useState("")

  const submit = (e) => {
    e.preventDefault();
    setName(e.target["name"].value);
    setPassword(e.target["password"].value);
    // save value
    console.log(e.target["name"].value);
    console.log(e.target["password"].value);
  };


// UseEffect
  useEffect(() => {
   
   //contitions
    if (name === "Nebuchadnezzar" && password === "12345") {
     setText("ad ve sifre hack edildi")
    }
    else if (name === "Nebuchadnezzar" && password !== "12345") {
      setText("ad  hack edildi")
    } 
    else if (password === "12345" && name !=="Nebuchadnezzar") {
      setText("sifre  hack edildi")
    }
    else {
      setText("bir daha sinayin")
    }
  }, [name, password]);



  // JSX 
  return (
    <>
    <form onSubmit={submit}>
      <div className="form-group">
        <h1>{text}</h1>
        <input
          type="text"
          name="name"
          className="form-control mb-4"
          placeholder="Name"
        />
       
        <input
          type="password"
          name="password"
          className="form-control mb-4"
          placeholder="Password"
        />
       
        <input
          type="submit"
          value="Submit"
          className="btn btn-warning form-control mt-2"
        />
      </div>
    </form>
    </>
  );
}

export default Form;
