import React, {useState} from 'react'

function Form () {
    const [name, setName]=useState("")
    const [password, setPassword]=useState("")

    const submit = (e) => {
        e.preventDefault()
        SetName(e.target['name'].value)
        SetPassword(e.target['password'].value)
    }

    
    useEffect(() => {

        let h1 = document.querySelector('h1')
        let nickNameHack = document.querySelector('.name-hack')
        let passwordHack = document.querySelector('.password-hack')
        if (nickName === "Nebuchadnezzar" && password === '12345') {
            h1.innerHTML = "Hack Olundu"
            nickNameHack.innerHTML = '  '
            passwordHack.innerHTML = "  "
        }
        else if (password === '12345') {
            passwordHack.innerHTML = "Şifrə hack Olundu"
            h1.innerHTML = " "
        }
        else if (nickName === "Nebuchadnezzar") {
            nickNameHack.innerHTML = "Nickname hack olundu"
            h1.innerHTML = " "
        }
        else {
            nickNameHack.innerHTML = " "
            passwordHack.innerHTML = " "

        }


    }, [nickName, password])
   
    return(
        <>
            <form style={{margin:"5rem"}} onSubmit={submit}>
            <div className="form-group">
                    <h1></h1>
                    <input type="text" name="name" className="form-control mb-4" placeholder="name" />
                    <label className='name-hack'></label>
                    <input type="password" name="password" className="form-control mb-4" placeholder="Password" />
                    <label className="password-hack"></label>
                    <input type="submit" value="Submit" className="btn btn-info form-control mt-2" />
                </div>
            </form>
        </>
    )
}
export default Form
