import react, {useEffect, useState}  from "react";


function Form(){
    // Usestate
    const [name, setName]= useState("")
    const [password, setPassword]= useState("")


    const submit=(e)=>{
        e.preventDefault()
        setName(e.target['name'].value)
        setPassword(e.target['password'].value)
        // save value 
        console.log(e.target['name'].value)
        console.log(e.target['password'].value)
    }

    return(
        <form onSubmit={submit}>
             <div className="form-group">
                    <h1></h1>
                    <input type="text" name="name" className="form-control mb-4" placeholder="Name" />
                    <h3 className='name-hack'></h3>
                    <input type="password" name="password" className="form-control mb-4" placeholder="Password" />
                    <h3 className="password-hack"></h3>
                    <input type="submit" value="Submit" className="btn btn-warning form-control mt-2" />
                </div>
        </form>
    )
}


export default Form
