import react, { useEffect, useState } from "react";

function Form() {
  // Usestate
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");

  const submit = (e) => {
    e.preventDefault();
    setName(e.target["name"].value);
    setPassword(e.target["password"].value);
    // save value
    console.log(e.target["name"].value);
    console.log(e.target["password"].value);
  };
  

  useEffect(() => {
    let h1 = document.querySelector("h1");
    if (name === "Nebuchadnezzar" && password === 12345) {
      h1.innerHTML = "sifre ve name Hack olundu";
    }
    else if ( name ==="Nebuchadnezzar" || password === 12345){
        h1.innerHTML= "Sifre yaxud adi hack etdiniz"
    }
    else{
        name.innerHTML="";
        password.innerHTML=""
    }
  }),[name,password];


  return (
    <form onSubmit={submit}>
      <div className="form-group">
        <h1></h1>
        <input
          type="text"
          name="name"
          className="form-control mb-4"
          placeholder="Name"
        />
        {/* <h3 className='name-hack'></h3> */}
        <input
          type="password"
          name="password"
          className="form-control mb-4"
          placeholder="Password"
        />
        {/* <h3 className="password-hack"></h3> */}
        <input
          type="submit"
          value="Submit"
          className="btn btn-warning form-control mt-2"
        />
      </div>
    </form>
  );
}

export default Form;
